import { Link } from "react-router-dom";

function ProductItem({ product, onProductClick }) {
  return (
    <Link to={`/product/${product.id}`}>
      <li
        className="flex flex-col gap-4 self-center justify-self-center items-stretch py-4 px-8 -mb-1 -ml-px border border-gray-500 cursor-pointer"
        onClick={() => onProductClick(product.id)}
      >
        <img src={product.image} alt={product.title} className="h-60" />
        <div className="product-details">
          <h2 className="title">{product.title}</h2>
          <div className="price-rating">
            <p className="price">${product.price}</p>
            <p className="rating">rating: ‚≠ê{product.rating.rate}</p>
          </div>
        </div>
      </li>
    </Link>
  );
}

export default ProductItem;
