import { getAuth, updateEmail, updatePassword } from "firebase/auth";

function ProfileEdit({
  newEmail,
  newPassword,
  onProfileUpdate,
  setEmail,
  setPassword,
}) {
  const auth = getAuth();
  const user = auth.currentUser;

  const updateNewEmail = async (newUpdatedEmail) => {
    try {
      await updateEmail(auth.currentUser, newUpdatedEmail);
      console.log("Email Updated");
    } catch (error) {
      console.error("An error occurred: ", error);
    }
  };
  updateNewEmail(newEmail);

  //   const updateNewPassword = async (newUpdatedPassword) => {
  //     const newUpdatedPassword = getASecureRandomPassword();
  //     try {
  //       await updatePassword(user, newUpdatedPassword);
  //       console.log("Update Successful");
  //     } catch (error) {
  //       console.log("An error occurred");
  //     }
  //   };
  //   updateNewPassword(newPassword);

  return (
    <div>
      {" "}
      <div>
        <h2>Edit Profile</h2>
        <div>
          <label>New Email:</label>
          <input
            type="email"
            value={newEmail}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label>New Password:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button onClick={onProfileUpdate}>Update Profile</button>
      </div>
    </div>
  );
}

export default ProfileEdit;
