import { Link } from "react-router-dom";
import PaymentForm from "../components/PaymentForm";

function CartCheckout({ cartItems }) {
  console.log(cartItems);

  const selectedCartItems = () => {
    const selectedItems = cartItems.filter((item) => item.isChecked);
    console.log(selectedItems);
    return selectedItems;
  };
  const selectedItems = selectedCartItems();

  return (
    <div className="checkout-section">
      <h1 className="text-5xl mb-24">Welcome to the Checkout Page!</h1>
      <div className="text-left mb-4">
        <Link to="/">&larr; Back to products</Link>
      </div>

      <div className="grid grid-cols-8  text-left border-2 border-orange-500 rounded-lg px-4 py-4">
        <PaymentForm />
        {/* {isLoading && <Loader />} */}

        <div className="px-8 flex flex-col gap-8 col-span-3">
          <h1 className="text-2xl">{selectedItems.length} ITEMS</h1>
          {selectedItems.map((item) => (
            <div key={item.id} className="grid grid-cols-3 gap-4 border-b pb-4">
              <img src={item.image} alt={item.title} className="w-24 h-24" />
              <div className="col-span-2">
                <p>$ {item.price}</p>
                <p>{item.title}</p>

                <p>{item.quantity}</p>
              </div>
            </div>
          ))}
          <div className="flex flex-col">
            <p className="flex justify-between py-2 border-y">
              Subtotal: <span className="text-lg">$ {2}</span>
            </p>
            <p className="flex justify-between py-2 border-b">
              Shipping: <span>$ {2}</span>
            </p>
            <p className="flex justify-between py-2 border-b">
              Taxes (5%): <span>$ {2}</span>
            </p>
            <p className="flex justify-between py-4 text-2xl">
              Total:<span>${2}</span>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CartCheckout;
