import { useEffect, useState } from "react";
import Header from "../components/Header";

function ProductDetails({ ProductDetail }) {
  const [selectedProduct, setselectedProduct] = useState([]);
  const productId = ProductDetail.params.id;
  //   if (!ProductDetails) return null;

  useEffect(
    function () {
      async function fetchProduct() {
        try {
          const res = await fetch(
            `https://fakestoreapi.com/products/${ProductDetail}`
          );
          const data = await res.json();
          setselectedProduct(data);
          console.log(data);
        } catch (error) {
          console.error("There was an issue fetching the data");
        }
      }
      fetchProduct();
    },
    [ProductDetail]
  );

  return (
    <>
      <Header />
      <div className="selected-product-details">
        <img src={selectedProduct.image} alt={selectedProduct.title} />
        <div>
          <h2>{selectedProduct.title}</h2>
          <p>Price: ${selectedProduct.price}</p>
          <p>Category: {selectedProduct.category}</p>
          <p>Description: {selectedProduct.description}</p>
          <span>
            Rating: ‚≠ê{selectedProduct.rating?.rate} (
            {selectedProduct.rating?.count || 0})
          </span>
        </div>
      </div>
    </>
  );
}

export default ProductDetails;
