import {
  getAuth,
  updateEmail,
  updatePassword,
  updateProfile,
} from "firebase/auth";
import { useState } from "react";

function ProfileEdit({
  newEmail,
  newPassword,
  newUserProfile,
  setEmail,
  setPassword,
  setUserProfile,
}) {
  const [editMode, setEditMode] = useState(false);
  const auth = getAuth();
  const user = auth.currentUser;

  const handleInputChange = (e) => {
    console.log("profile update");

    const { name, value } = e.target;
    setUserProfile((prevProfile) => ({
      ...prevProfile,
      [name]: value,
    }));
  };

  const handleSave = async (e) => {
    e.preventDefault();

    // Destructure user object (avoid modifying original state)
    const { email, password, currentPassword } = { ...user };
    const newEmail = email !== user.email ? email : null; // Update email if changed
    const newPassword = password ? password : null; // Update password if changed

    if (!currentPassword) {
      // Handle missing current password error
      return;
    }

    try {
      const credential = reauthenticateWithCredential(
        getAuth(auth).currentUser,
        signInWithEmailAndPassword(getAuth(auth), user.email, currentPassword)
      );
      await credential.user.updateEmail(newEmail); // Update email if necessary
      if (newPassword) {
        await updatePassword(getAuth(auth).currentUser, newPassword); // Update password if necessary
      }
      setUser({ ...user, email: newEmail || email, password: "" }); // Update user state with changes (clear new password)
      setEditMode(false);
    } catch (error) {
      console.error("Error updating profile:", error);
      // Handle errors (e.g., invalid email format, incorrect current password)
    }
  };

  return (
    <div>
      {" "}
      {/* Edit Button */}
      <button onClick={() => setEditMode(!editMode)}>
        {editMode ? "Save Changes" : "Edit Profile"}
      </button>
      {/* Edit Form */}
      {editMode && (
        <form onSubmit={handleSave}>
          <input type="text" defaultValue={user?.displayName} />
          <input
            type="email"
            defaultValue={user?.email}
            disabled={!editMode}
          />{" "}
          {/* Email */}
          <input
            type="password"
            placeholder="New Password"
            onChange={(e) => setPassword(e.target.value)}
          />{" "}
          {/* New Password */}
          <input
            type="password"
            placeholder="Current Password"
            onChange={(e) => setCurrentPassword(e.target.value)}
          />{" "}
          {/* Current Password for Verification */}
          <button type="submit">Save</button>
        </form>
      )}
    </div>
  );
}

export default ProfileEdit;

