import { useState, useEffect } from "react";
import { ref, get } from "firebase/database";
import { db } from "../firebase";
import { useAuth } from "../components/AuthContext";

function Orders({ orderDetails }) {
  const { currentUser } = useAuth();
  const [order, setOrder] = useState([]);
  const userUid = currentUser.uid;

  // const newOrder = [];
  // newOrder.push(order);
  // console.log("new order", newOrder);

  // const {} = order;

  console.log("order items: ", order);

  const getOrderData = async (userId) => {
    try {
      const orderRef = ref(db, `orders/${userId}`);
      const snapshot = await get(orderRef);

      if (snapshot.exists()) {
        return snapshot.val();
      }
      return null;
    } catch (error) {
      console.log("Error fetching Order data: ", error.message);
      return null;
    }
  };

  useEffect(() => {
    // Code to run on component mount
    const fetchOrder = async () => {
      const data = await getOrderData(userUid);
      if (data) {
        const orders = Object.values(data);
        console.log("orders array", orders);

        // Object.values(data).map((order) => {
        //   setOrder((oldArray) => [...oldArray, order]);
        // });
        setOrder(orders);
      }
    };

    fetchOrder();
  }, []);

  return (
    <div>
      <h1 className="text-5xl text-center py-8 border-b-2">
        Your Recent Orders
      </h1>
      <div className="checkout-section">
        <h1>Orders</h1>{" "}
        <ul>
          {order.map((item) => (
            <li key={item.orderId} item={item} className="py-4 text-left">
              <p>Order Id: {item.orderId}</p>
              <p>Payment type: {item.paymentOption}</p>
              <p>Order Date: {item.orderDate}</p>
              <p>Billing Address: {item.address}</p>
              <p>Email: {item.email}</p>
              <p>Customer Name: {item.name}</p>
              <p>
                Cart Item:{" "}
                <ul>
                  {item.items.map((item) => (
                    <>
                      <li>{item.title}</li>
                      <li>{item.image}</li>
                    </>
                  ))}
                </ul>
              </p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Orders;
