function ProductItem({ product }) {
  return (
    <li className="product">
      <img src={product.image} alt={product.title} />
      <h2>{product.title}</h2>
      <p>{product.price}</p>
      <p>{product.category}</p>
      {/* <p>{product.description}</p> */}
    </li>
  );
}

export default ProductItem;
