import { useEffect, useState } from "react";

function CartProduct({ item, removeItem, updateQuantity, updateSubtotal }) {
  const [quantity, setQuantity] = useState(1);

  // UPDATE SUBTOTAL
  useEffect(() => {
    updateSubtotal(item.id, quantity * item.price);
  }, [quantity]);

  // HANDLE QUANTITY CHANGE
  const handleQuantityChange = (newQuantity) => {
    if (newQuantity >= 1) {
      setQuantity(newQuantity);
      updateQuantity(item.id, newQuantity);
    }
  };

  function dec() {
    if (quantity < 2) return 0;
    setQuantity(quantity - 1);
  }
  function inc() {
    setQuantity(quantity + 1);
  }
  const subtotal = Number(item.price * quantity).toFixed(2);

  return (
    <tr className="cart-item">
      <td>
        <input type="checkbox" />
      </td>
      <td colSpan="2" className="productImg">
        <img src={item.image} alt={item.title} />
        <h3>{item.title}</h3>
      </td>
      <td>
        <p>${item.price}</p>
      </td>
      <td>
        <div className="quantity">
          <button onClick={dec}>-</button>
          <span>{quantity}</span>
          <button onClick={inc}>+</button>
        </div>
      </td>
      <td>
        <p>$ {subtotal}</p>
      </td>
      <td>
        <button onClick={() => removeItem(item)}>Delete</button>
      </td>
    </tr>
  );
}

export default CartProduct;
