import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Loader from "../components/Loader";
import Header from "../components/Header";

function ProductDetails({ setIsLoading, isLoading, addToCart }) {
  const { id } = useParams();
  const [selectedProduct, setSelectedProduct] = useState([]);
  const [quantity, setQuantity] = useState(1);

  const updatedQuantity = (itemId, newQuantity) => {
    if (id === itemId) {
      const newSubtotal = item.price * newQuantity;
      return { ...item, quantity: newQuantity, subtotal: newSubtotal };
    }
    return item;
  };

  const dec = () => {
    if (quantity < 2) return;
    setQuantity(quantity - 1);
  };

  const inc = () => {
    setQuantity(quantity + 1);
  };

  const handleAddToCart = () => {
    if (selectedProduct) {
      addToCart(selectedProduct, quantity);
    }
  };

  useEffect(
    function () {
      async function fetchProduct() {
        try {
          setIsLoading(true);
          const res = await fetch(`https://fakestoreapi.com/products/${id}`);
          const data = await res.json();
          setSelectedProduct(data);
          console.log(data);
        } catch (error) {
          console.error("There was an issue fetching the data");
        } finally {
          setIsLoading(false);
        }
      }
      fetchProduct();
    },
    [id, setIsLoading]
  );

  return (
    <>
      <Header />
      {isLoading ? (
        <Loader />
      ) : (
        <div className="select-products-section">
          <div className="selected-product-details">
            <img src={selectedProduct.image} alt={selectedProduct.title} />
            <div className="selected-info">
              <h2 className="text-3xl font bold under">
                {selectedProduct.title}
              </h2>
              <p>
                Price:{" "}
                <span className="text-lg font-bold">
                  ${selectedProduct.price}
                </span>
              </p>
              <p>Category: {selectedProduct.category}</p>
              <p className="border border-gray-400 px-2 py-2 rounded-xl">
                <span>
                  Description: <br />
                  {selectedProduct.description}
                </span>
              </p>
              <span>
                Rating: ‚≠ê {selectedProduct.rating?.rate} (
                {selectedProduct.rating?.count || 0})
              </span>
              <div>
                <span className="mr-3">Qty: </span>
                <button onClick={dec} className="py-0.5 px-2 mr-2">
                  -
                </button>
                <span>{quantity}</span>
                <button onClick={inc} className="py-0.5 px-2 ml-2">
                  +
                </button>
              </div>
              <button onClick={handleAddToCart}>Add to Cart</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default ProductDetails;
