import { Link } from "react-router-dom";
import { useState } from "react";
import Loader from "./Loader";

function ProductItem({ product, onProductClick, loading }) {
  return (
    <Link to={`/product/${product.id}`}>
      <li
        className="flex flex-col gap-4 self-center justify-self-center py-4 px-8 -mb-px mt-0 -ml-px mr-0 border border-y-[0.5] border-x border-gray-500 cursor-pointer"
        onClick={() => onProductClick(product.id)}
      >
        <img src={product.image} alt={product.title} className="h-60" />
        <div className="h-36">
          <h2 className="title">{product.title}</h2>
          <div className="flex justify-between items-center">
            <p className="price">
              $ <span className="text-xl">{product.price}</span>
            </p>
            <p className="rating">rating: ‚≠ê{product.rating.rate}</p>
          </div>
        </div>
      </li>
    </Link>
  );
}

export default ProductItem;
