function OrderSummary({ item, selectedCartItems }) {
  const selectedItems = selectedCartItems();

  return (
    <div>
      {selectedItems.map((item) => (
        <div
          key={item.id}
          className="grid grid-cols-3 gap-4 border-b pb-4 text-sm"
        >
          <img src={item.image} alt={item.title} className="w-24 h-24" />
          <div className="col-span-2 flex flex-col gap-y-1">
            <p>
              ${" "}
              <span className="text-lg font-semibold italic  text-gray-500">
                {item.price}
              </span>
            </p>
            <p className="font-semibold">{item.title}</p>

            {item.quantity > 1 ? (
              <p>
                <span className="text-lg font-semibold italic  text-gray-500">
                  {item.quantity}
                </span>{" "}
                Items Added
              </p>
            ) : (
              <p>
                <span className="text-lg font-semibold italic  text-gray-500">
                  {item.quantity}
                </span>{" "}
                Item Added
              </p>
            )}
            <p>
              Subtotal: $
              <span className="text-lg font-semibold italic  text-gray-500">
                {" "}
                {item.subtotal}
              </span>
            </p>
          </div>
        </div>
      ))}
      ;
    </div>
  );
}

export default OrderSummary;
