import { getAuth, updateEmail, updatePassword } from "firebase/auth";

function ProfileEdit({
  newEmail,
  newPassword,
  newUserName,
  onProfileUpdate,
  setEmail,
  setPassword,
  setUserName,
}) {
  const auth = getAuth();
  const user = auth.currentUser;
  setPassword(getASecureRandomPassword());

  const updateNewEmail = async (newUpdatedEmail) => {
    try {
      await updateEmail(user, newUpdatedEmail);
      console.log("Email Updated");
    } catch (error) {
      console.error("An error occurred: ", error);
    }
  };
  updateNewEmail(newEmail);

  const updatePassword = async (newPassword) => {
    try {
      await updatePassword(user, newPassword);
      console.log("Password Update Successful");
    } catch (error) {
      console.log("An error occurred");
    }
  };
  updatePassword(newPassword);

  return (
    <div>
      {" "}
      <div>
        <h2 className="mb-4 text-lg font-semibold">Edit Profile</h2>
        <div className="mb-4">
          <label className="font-semibold mb-2">Username:</label>
          <input
            type="text"
            value={newUserName}
            onChange={(e) => setUserName(e.target.value)}
            className="py-1 px-2 border border-gray-500 ml-4"
          />
        </div>
        <div className="mb-4">
          <label className="font-semibold mb-2">New Email:</label>
          <input
            type="email"
            value={newEmail}
            onChange={(e) => setEmail(e.target.value)}
            className="py-1 px-2 border border-gray-500 ml-4"
          />
        </div>
        <div className="mb-4">
          <label className="font-semibold">New Password:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setPassword(e.target.value)}
            className="py-1 px-2 border border-gray-500 ml-4"
          />
        </div>
        <button onClick={onProfileUpdate}>Update Profile</button>
      </div>
    </div>
  );
}

export default ProfileEdit;
