import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Loader from "../components/Loader";
import Header from "../components/Header";

function ProductDetails({ setIsLoading, isLoading, addToCart }) {
  const { id } = useParams();
  const [selectedProduct, setSelectedProduct] = useState([]);

  useEffect(
    function () {
      async function fetchProduct() {
        try {
          setIsLoading(true);
          const res = await fetch(`https://fakestoreapi.com/products/${id}`);
          const data = await res.json();
          setSelectedProduct(data);
          console.log(data);
        } catch (error) {
          console.error("There was an issue fetching the data");
        } finally {
          setIsLoading(false);
        }
      }
      fetchProduct();
    },
    [id, setIsLoading]
  );

  return (
    <>
      <Header />
      {isLoading ? (
        <Loader />
      ) : (
        <div className="select-products-section">
          <div className="selected-product-details">
            <img src={selectedProduct.image} alt={selectedProduct.title} />
            <div className="selected-info">
              <h2>{selectedProduct.title}</h2>
              <p>Price: ${selectedProduct.price}</p>
              <p>Category: {selectedProduct.category}</p>
              <p>
                <span className="description">
                  Description: <br />
                  {selectedProduct.description}
                </span>
              </p>
              <span>
                Rating: ‚≠ê {selectedProduct.rating?.rate} (
                {selectedProduct.rating?.count || 0})
              </span>
              <button onClick={() => addToCart(selectedProduct)}>
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default ProductDetails;
