import { useState } from "react";
import { Link } from "react-router-dom";

function OrderConfirm({ orderDetails, selectedItems }) {
  const [loading, setLoading] = useState(false);
  console.log("order details in confirm: ", orderDetails);
  // console.log(orderDetails.items);

  // if (!orderDetails) {
  //   return <div>Loading...</div>;
  // }

  return (
    <div className="orderConfirm-section">
      <p className="text-lg">Thank you!</p>
      <h1 className="text-7xl font-bold my-8">It's on the way!</h1>
      <p className="text-gray-400 text-xl">
        Your order #2382038434 has shipped and will be with you soon.
      </p>

      <div className="grid grid-cols-8 gap-x-8 my-16 border-t-2 py-4">
        <ul className="col-span-5 border-b-2 border-gray-400">
          {loading ? (
            <p>Loading...</p>
          ) : (
            selectedItems.map((item) => (
              <OrderConfirmItem key={item.id} item={item} />
            ))
          )}
        </ul>

        <div className="text-left col-span-3 pl-4 flex flex-col gap-1">
          <h2 className="font-semibold mb-2">Shipping Address</h2>
          <p>Name: {orderDetails.name}</p>
          <p>Email: {orderDetails.email}</p>
          <p>Address: {orderDetails.address}</p>
          <p>Payment Method: {orderDetails.paymentOption}</p>
        </div>
      </div>
      <div className="flex flex-col gap-8 w-1/2 border-t-2 py-4 my-8">
        <div className="flex justify-between">
          <span>Subtotal</span>
          <span>$36.00</span>
        </div>
        <div className="flex justify-between">
          <span>Shipping</span>
          <span>$10.00</span>
        </div>
        <div className="flex justify-between text-xl text-orange-600">
          <span>Total</span>
          <span>$46.00</span>
        </div>
      </div>
      <Link to="/" className="text-orange-700 font-semibold text-lg">
        &larr; Continue shopping
      </Link>
    </div>
  );
}

function OrderConfirmItem({ item }) {
  return (
    <div>
      {/* <li>{item.title}</li> */}
      <li className="flex justify-between gap-6 py-4">
        <img src={item.image} alt="An image" className="w-48 h-48 basis-1/4" />
        <div className="flex flex-col gap-4 basis-1/2">
          <span className="text-lg font-semibold">{item.title}</span>
          <span>{item.description}</span>
          <span>Quantity: {item.quantity}</span>
        </div>
        <span className="text-2xl basis-1/4">$20</span>
      </li>
    </div>
  );
}

export default OrderConfirm;
