import { useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

function ProductCheckout({ products }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [cardNumber, setCardNumber] = useState("");
  const [cardName, setCardName] = useState("");
  const [dateExpired, setDateExpired] = useState("");
  const [cvc, setCvc] = useState("");
  const [paymentOption, setPaymentOption] = useState("credit");

  const { id, quantity } = useParams();
  // Convert quantity to a number
  const quantityValue = parseInt(quantity, 10);

  // Fetch the specific product using the id
  // Example: Fetch the product from the products array based on the id
  // const product = products.find((item) => item.id === parseInt(id));

  const shippingFee = 10;
  // const totalAmount = product.price * quantityValue;
  // const grandTotal = totalAmount + shippingFee;
  console.log(id);
  console.log(quantityValue);
  // console.log(product);

  // if (!product) {
  //   return <div>Product not found</div>;
  // }

  return (
    <div className="checkout-section">
      <h1>Welcome to the Checkout Page!</h1>
      <Link to="/">&larr; Back to products</Link>
      <div className="grid grid-cols-2  text-left border-2 border-orange-500 rounded-lg px-4 py-4">
        <div className="pr-4 border-r-2 border-r-orange-200">
          <form className="mb-8">
            <div className="mb-16">
              <h1 className="text-2xl mb-4">Contact Information</h1>
              <div className="flex gap-4">
                <input
                  type="text"
                  placeholder="Name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="block w-full mb-4 py-2 px-2  border-2 rounded-lg "
                />
              </div>
              <div className="flex gap-4">
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="block w-full mb-4 py-2 px-2  border-2 rounded-lg "
                />
                <input
                  type="tel"
                  placeholder="Phone (optional)"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  className="block w-full mb-4 py-2 px-2  border-2 rounded-lg"
                />
              </div>
              <div>
                <input
                  type="text"
                  placeholder="Address"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  className="block w-full mb-4 py-2 px-2  border-2 rounded-lg "
                />
              </div>
            </div>

            <div>
              <h1 className="text-2xl mb-4">Payment Options</h1>
              <div className="flex gap-8 mb-4">
                <div className="flex items-center">
                  <input
                    type="radio"
                    id="card"
                    name="paymentOption"
                    value="credit"
                    checked={paymentOption === "credit"}
                    onChange={() => setPaymentOption("credit")}
                    required
                    className="mr-2"
                  />
                  <label htmlFor="card">Credit / Debit Card</label>
                </div>
                <div className="flex items-center">
                  <input
                    type="radio"
                    id="paypal"
                    name="paymentOption"
                    value="paypal"
                    checked={paymentOption === "paypal"}
                    onChange={() => setPaymentOption("paypal")}
                    className="mr-2"
                  />
                  <label htmlFor="paypal">Paypal</label>
                </div>
                <div className="flex items-center">
                  <input
                    type="radio"
                    id="other"
                    name="paymentOption"
                    value="other"
                    checked={paymentOption === "other"}
                    onChange={() => setPaymentOption("other")}
                    className="mr-2"
                  />
                  <label htmlFor="other">Other</label>
                </div>
              </div>

              <div>
                <input
                  type="text"
                  placeholder="Card Number"
                  value={cardNumber}
                  onChange={(e) => setCardNumber(e.target.value)}
                  className="block w-full mb-4 py-2 px-2  border-2 rounded-lg "
                />
              </div>
              <div>
                <input
                  type="text"
                  placeholder="Card Name"
                  value={cardName}
                  onChange={(e) => setCardName(e.target.value)}
                  className="block w-full mb-4 py-2 px-2  border-2 rounded-lg "
                />
              </div>
              <div className="flex gap-4">
                <input
                  type="text"
                  placeholder="Expration date (MM/YY)"
                  value={dateExpired}
                  onChange={(e) => setDateExpired(e.target.value)}
                  className="flex-1 mb-4 py-2 px-2  border-2 rounded-lg "
                />
                <input
                  type="text"
                  placeholder="CVC"
                  value={cvc}
                  onChange={(e) => setCvc(e.target.value)}
                  className="flex-2 mb-4 py-2 px-2  border-2 rounded-lg hover:text"
                />
              </div>
            </div>

            <button className="mt-8">Place Order</button>
          </form>
        </div>

        <div>
          <h1 className="text-2xl pl-4 mb-8">Order Summary</h1>
          <div>
            {/* <img src={product.image} alt={product.title} />
            <h2>{product.title}</h2>
            <p>Price: $ {product.price}</p>
            <p>Quantity: {quantityValue}</p>
            <p>Subtotal: $ {totalAmount.toFixed(2)}</p>
            <p>Shipping: $ {shippingFee.toFixed(2)}</p> */}
          </div>
          {/* <div>Total: ${grandTotal.toFixed(2)}</div> */}
        </div>
      </div>
    </div>
  );
}

export default ProductCheckout;
