import { Link } from "react-router-dom";
import PaymentForm from "../components/PaymentForm";

function CartCheckout() {
  return (
    <div className="checkout-section">
      <h1 className="text-5xl mb-24">Welcome to the Checkout Page!</h1>
      <div className="text-left mb-4">
        <Link to="/">&larr; Back to products</Link>
      </div>

      <div className="grid grid-cols-3  text-left border-2 border-orange-500 rounded-lg px-4 py-4">
        <PaymentForm />
        {/* {isLoading && <Loader />} */}

        <div className="px-8 flex flex-col gap-8">
          <h1 className="text-2xl">Order Summary</h1>

          <div className="flex flex-col h-full justify-between gap-16">
            <div className="grid grid-cols-2 gap-8">
              <img
              // src={selectedProduct.image}
              // alt={selectedProduct.title}
              // className="border border-orange-200 rounded-xl p-2"
              />
              <div>
                {/* <h2 className="text-xl mb-4">{selectedProduct.title}</h2> */}
                <h2>
                  {/* Rating: &#9733;{" "} */}
                  <span className="text-lg font-semibold">
                    {/* {selectedProduct.rating?.rate} / */}
                  </span>
                  {/* 5 */}
                </h2>
                {/* <p>Qty: {quantityValue}</p> */}
                <p className="mt-8">
                  {/* $ <span className="text-2xl">{selectedProduct.price}</span> */}
                </p>
              </div>
            </div>
            <div className="flex flex-col">
              {/* <p className="flex justify-between py-2 border-y">
                Subtotal:{" "}
                <span className="text-lg">$ {totalAmount.toFixed(2)}</span>
              </p>
              <p className="flex justify-between py-2 border-b">
                Shipping: <span>$ {shippingFee.toFixed(2)}</span>
              </p>
              <p className="flex justify-between py-2 border-b">
                Taxes (5%): <span>$ {taxes.toFixed(2)}</span>
              </p>
              <p className="flex justify-between py-4 text-2xl">
                Total:<span>${grandTotal.toFixed(2)}</span>
              </p> */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CartCheckout;
