import { useState, useEffect } from "react";
import CartProduct from "../components/CartProduct";

function Cart({ cartItems, removeItem, setCartItems }) {
  const [subtotals, setSubtotals] = useState({});
  const [totalSubtotals, setTotalSubtotals] = useState(0);
  const [grandTotal, setGrandTotal] = useState(0);
  const shippingFee = 0;

  // UPDATE QUANTITY
  const updateQuantity = (itemId, newQuantity) => {
    const updatedItems = cartItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
    setCartItems(updatedItems);
  };

  // UPDATE SUBTOTAL
  const updateSubtotal = (itemId, newSubtotal) => {
    setSubtotals((prevSubtotals) => ({
      ...prevSubtotals,
      [itemId]: newSubtotal,
    }));
  };

  useEffect(() => {
    const newGrandTotal = Object.values(subtotals).reduce(
      (total, subtotal) => total + subtotal,
      0
    );
    setGrandTotal(newGrandTotal);
  }, [subtotals]);

  useEffect(() => {
    // Calculate the total of all subtotals
    const newTotalSubtotals = Object.values(subtotals).reduce(
      (total, subtotal) => total + subtotal,
      0
    );
    setTotalSubtotals(newTotalSubtotals); // Update the total subtotals state

    // Calculate the grand total by adding the shipping fee to the total subtotals
    const newGrandTotal = newTotalSubtotals + shippingFee;
    setGrandTotal(newGrandTotal); // Update the grand total state
  }, [subtotals]);

  return (
    <div className="cart-section">
      <>
        <div className="cart">
          <h1>Welcome to the Cart!</h1>
          <p>Make sure you add items soon! 😉</p>
        </div>
        <table className="cart-table">
          <thead>
            <tr>
              {/* <th>Img</th> */}
              <th className="product-column">Product</th>
              <th>Price</th>
              <th>Qty</th>
              <th>Subtotal</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item) => (
              <CartProduct
                key={item.id}
                item={item}
                removeItem={removeItem}
                updateSubtotal={updateSubtotal}
                updateQuantity={updateQuantity}
              />
            ))}
          </tbody>
        </table>
        <div className="cart-checkout">
          <div className="coupon">
            <input type="text" placeholder="Enter Coupon Code" />
            <button>Apply Coupon</button>
          </div>
          <div></div>
          <div className="cart-total">
            <h3>Cart Total</h3>
            <div className="details">
              {cartItems.map((item) => (
                <div className="flex detail">
                  <span>Subtotal</span>
                  <span>${subtotals[item.id]}</span>
                </div>
              ))}
              <div className="flex detail">
                <span>Shipping</span>
                <span>{shippingFee > 0 ? shippingFee : "Free"}</span>
              </div>
              <div className="flex detail total">
                <span>Total</span>
                <span>${grandTotal.toFixed(2)}</span>
              </div>
            </div>

            <div className="btn-checkout">
              <button>Proceed to checkout</button>
            </div>
          </div>
        </div>
      </>
    </div>
  );
}

export default Cart;
