import { useState, useEffect } from "react";
import { ref, get } from "firebase/database";
import { db } from "../firebase";
import { useAuth } from "../components/AuthContext";

function Orders({ orderDetails }) {
  const { currentUser } = useAuth();
  const [order, setOrder] = useState([]);
  const userUid = currentUser.uid;

  // const newOrder = [];
  // newOrder.push(order);
  // console.log("new order", newOrder);

  // const {} = order;

  console.log("order items: ", order);

  const getOrderData = async (userId) => {
    try {
      const orderRef = ref(db, `orders/${userId}`);
      const snapshot = await get(orderRef);

      if (snapshot.exists()) {
        return snapshot.val();
      }
      return null;
    } catch (error) {
      console.log("Error fetching Order data: ", error.message);
      return null;
    }
  };

  useEffect(() => {
    // Code to run on component mount
    const fetchOrder = async () => {
      const data = await getOrderData(userUid);
      if (data) {
        const orders = Object.values(data);
        console.log("orders array", orders);

        // Object.values(data).map((order) => {
        //   setOrder((oldArray) => [...oldArray, order]);
        // });
        setOrder(orders);
      }
    };

    fetchOrder();
  }, []);

  return (
    <div>
      <h1 className="text-5xl text-center py-8 border-b-2">
        Your Recent Orders
      </h1>
      <div className="checkout-section">
        <h1 className="text-3xl mb-4">Orders</h1>{" "}
        <ul>
          {order.map((item) => (
            <li
              key={item.orderId}
              item={item}
              className="py-4 mb-8 px-16 text-left border"
            >
              <div className="flex justify-between mb-4">
                <p>Order Id: {item.orderId}</p>
                <p>Payment type: {item.paymentOption}</p>
                <p>Order Date: {item.orderDate}</p>
              </div>
              <div className="flex justify-between mb-8">
                <p>Billing Address: {item.address}</p>
                <p>Email: {item.email}</p>
                <p>Customer Name: {item.name}</p>
              </div>

              <p>
                Cart Item:{" "}
                <ul>
                  {item.items.map((item) => (
                    <li
                      key={item.id}
                      item={item}
                      className="grid grid-cols-8 gap-8 py-4 border-b-2 border-gray-300"
                    >
                      <img
                        src={item.image}
                        alt="An image"
                        className="col-span-2"
                      />
                      <div className="flex flex-col gap-4 col-span-5">
                        <span className="text-lg font-semibold">
                          {item.title}
                        </span>
                        <span className="text-gray-500">
                          {item.description}
                        </span>
                        <span className="font-semibold">
                          Qty:{" "}
                          <span className="text-lg mt-auto">
                            {item.quantity}
                          </span>
                        </span>
                      </div>
                      <span className=" col-span-1 justify-self-end self-end">
                        $
                        <span className="text-3xl font-semibold">
                          {item.price}
                        </span>
                      </span>
                    </li>
                  ))}
                </ul>
              </p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Orders;
