import { useState, useEffect } from "react";
import { ref, get } from "firebase/database";
import { db } from "../firebase";
import { useAuth } from "../components/AuthContext";

function Orders({ orderDetails }) {
  const { currentUser } = useAuth();
  const [order, setOrder] = useState([]);
  const userUid = currentUser.uid;

  // const newOrder = [];
  // newOrder.push(order);
  // console.log("new order", newOrder);

  // const {} = order;

  console.log("order items: ", order);

  const getOrderData = async (userId, email) => {
    try {
      const orderRef = ref(db, `orders/${userId}/${email}`);
      const snapshot = await get(orderRef);

      if (snapshot.exists()) {
        return snapshot.val();
      }
      return null;
    } catch (error) {
      console.log("Error fetching Order data: ", error.message);
      return null;
    }
  };

  useEffect(() => {
    // Code to run on component mount
    const fetchOrder = async () => {
      const data = await getOrderData(userUid, orderDetails?.email);
      if (data) {
        setOrder(data);
      }
    };

    fetchOrder();
  }, []);

  return (
    <div>
      <h1 className="text-5xl text-center py-8 border-b-2">
        Your Recent Orders
      </h1>
      <div className="checkout-section">Orders</div>
      <ul>
        {/* {order.map((item) => (
          <li key={item.orderId} item={item}>
            <p>{item.address}</p>
          </li>
        ))} */}
      </ul>
    </div>
  );
}

export default Orders;
