import { Link } from "react-router-dom";
import PaymentForm from "../components/PaymentForm";

function CartCheckout({ cartItems }) {
  console.log(cartItems);

  const selectedCartItems = () => {
    const selectedItems = cartItems.filter((item) => item.isChecked);
    console.log(selectedItems);
    return selectedItems;
  };
  const selectedItems = selectedCartItems();

  // TOTAL-SUBTOTAL
  const calculateSelectedSubtotals = () => {
    const selectedItems = cartItems.filter((item) => item.isChecked);
    const selectedSubtotal = selectedItems.reduce(
      (total, item) => total + Number(item.subtotal),
      0
    );
    return selectedSubtotal;
  };
  const totalSubtotals = Number(calculateSelectedSubtotals().toFixed(2));

  // GRAND TOTAL
  const shippingFee = 10;
  const grandTotal = Number(totalSubtotals + shippingFee).toFixed(2);

  return (
    <div className="checkout-section">
      <h1 className="text-5xl mb-24">Welcome to the Checkout Page!</h1>
      <div className="text-left mb-4">
        <Link to="/cart">&larr; Back to cart</Link>
      </div>

      <div className="grid grid-cols-8  text-left border-2 border-orange-500 rounded-lg px-4 py-4">
        <PaymentForm />
        {/* {isLoading && <Loader />} */}

        <div className="px-8 flex flex-col gap-8 col-span-3">
          <h1 className="text-2xl">{selectedItems.length} ITEMS</h1>
          {selectedItems.map((item) => (
            <div
              key={item.id}
              className="grid grid-cols-3 gap-4 border-b pb-4 text-sm"
            >
              <img src={item.image} alt={item.title} className="w-24 h-24" />
              <div className="col-span-2 flex flex-col gap-y-1">
                <p>
                  ${" "}
                  <span className="text-lg font-semibold italic  text-gray-500">
                    {item.price}
                  </span>
                </p>
                <p className="font-semibold">{item.title}</p>

                {item.quantity > 1 ? (
                  <p>
                    <span className="text-lg font-semibold italic  text-gray-500">
                      {item.quantity}
                    </span>{" "}
                    Items Added
                  </p>
                ) : (
                  <p>
                    <span className="text-lg font-semibold italic  text-gray-500">
                      {item.quantity}
                    </span>{" "}
                    Item Added
                  </p>
                )}
                <p>
                  Subtotal: $
                  <span className="text-lg font-semibold italic  text-gray-500">
                    {" "}
                    {item.subtotal}
                  </span>
                </p>
              </div>
            </div>
          ))}
          <div className="flex flex-col mt-16">
            <p className="flex justify-between py-2 border-y">
              Subtotal:{" "}
              <span className="text-lg">$ {totalSubtotals.toFixed(2)}</span>
            </p>
            <p className="flex justify-between py-2 border-b">
              Shipping: <span>$ {2}</span>
            </p>
            <p className="flex justify-between py-2 border-b">
              Taxes (5%): <span>$ {2}</span>
            </p>
            <p className="flex justify-between py-4 text-2xl">
              Total:<span>${2}</span>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CartCheckout;
