import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import { useAuth } from "./components/AuthContext";
import { initializeApp } from "firebase/app";
import { getDatabase } from "firebase/database";
import HomePage from "./pages/HomePage";
import Cart from "./pages/Cart";
import Wishlist from "./pages/Wishlist";
import ProductDetails from "./pages/ProductDetails";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import ProductCheckout from "./pages/ProductCheckout";
import CartCheckout from "./pages/CartCheckout";
import OrderConfirm from "./pages/OrderConfirm";
import ProductOrderConfirm from "./pages/ProductOrderConfirm";
import Login from "./pages/Login";
import Register from "./pages/Register";
import ProfilePage from "./pages/ProfilePage";

const firebaseConfig = {
  apiKey: "AIzaSyAFHx-hoK6-ogQPckh1-M3BZVSDW3Wadv8 ",
  authDomain: "e-store-f957f.firebaseapp.com",
  projectId: "e-store-f957f",
  storageBucket: "e-store-f957f.appspot.com",
  messagingSenderId: "633284440662",
  appId: "1:633284440662:web:632ec5d84b9583c41f41f7",
  databaseURL:
    "https://e-store-f957f-default-rtdb.asia-southeast1.firebasedatabase.app/",
};

const app = initializeApp(firebaseConfig);
const db = getDatabase(app);

function App() {
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [orderDetails, setOrderDetails] = useState(null);

  const { currentUser } = useAuth();

  const userProfile = {
    ...currentUser,
    cartItems,
  };

  console.log(db);

  // console.log("userProfile: ", userProfile);

  // UPDATE ITEM QUANTITY
  const updateQuantity = (itemId, newQuantity) => {
    const updatedItems = cartItems.map((item) => {
      if (item.id === itemId) {
        const newSubtotal = item.price * newQuantity;
        return { ...item, quantity: newQuantity, subtotal: newSubtotal };
      }
      return item;
    });
    setCartItems(updatedItems);
    localStorage.setItem("cart", JSON.stringify(updatedItems));
  };

  // SELECT ITEM FOR PREVIEW
  function handleProductClick(id) {
    setSelectedProduct((selectedProduct) =>
      id === selectedProduct ? null : id
    );
  }

  // ADD TO CART
  const addToCart = (item, quantity) => {
    const existingCartItem = cartItems.find(
      (cartItem) => cartItem.id === item.id
    );

    if (existingCartItem) {
      // Item already exists in the cart, update the quantity
      const updatedCartItems = cartItems.map((cartItem) =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + quantity }
          : cartItem
      );
      setCartItems(updatedCartItems);
    } else {
      // Item doesn't exist in the cart, add it
      const newItem = {
        ...item,
        quantity: quantity,
        subtotal: item.price * quantity,
        isChecked: false,
      };
      const updatedCart = [...cartItems, newItem];
      setCartItems(updatedCart);
    }
    alert(`${quantity} ${item.title} added to cart`);
    localStorage.setItem("cart", JSON.stringify(cartItems));
  };

  const selectedProductItems = () => {
    const selectedItems = cartItems.filter((item) => item.isChecked);
    return selectedItems;
  };
  const selectedItems = selectedProductItems();

  // TOTAL-SUBTOTAL
  const calculateSelectedSubtotals = () => {
    const selectedItems = cartItems.filter((item) => item.isChecked);
    const selectedSubtotal = selectedItems.reduce(
      (total, item) => total + Number(item.subtotal),
      0
    );
    return selectedSubtotal;
  };
  const totalSubtotals = calculateSelectedSubtotals();

  // REMOVE FROM CART
  const removeCartItem = (product) => {
    const updatedCart = cartItems.filter((item) => item.id !== product.id);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // DELETE ALL ITEMS FROM CART
  const removeAll = () => {
    setCartItems([]);
    const updatedCart = setCartItems([]);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  // PERSIST DATA IN LOCAL STORAGE
  useEffect(() => {
    const storedCartItems = localStorage.getItem("cart");
    if (storedCartItems) {
      setCartItems(JSON.parse(storedCartItems));
    }
  }, []);

  useEffect(() => {
    // RETRIEVE orderDetails from local storage
    const storedOrderDetails = localStorage.getItem("orderDetails");
    if (storedOrderDetails) {
      setOrderDetails(JSON.parse(storedOrderDetails));
    }
  }, []);

  useEffect(function () {
    async function fetchProducts() {
      try {
        setIsLoading(true);
        const res = await fetch("https://fakestoreapi.com/products");
        const data = await res.json();
        setProducts(data);
      } catch (error) {
        console.error("There was an issue fetching the data");
      } finally {
        setIsLoading(false);
      }
    }
    fetchProducts();
  }, []);

  return (
    <div className="mt-12">
      <BrowserRouter>
        <Navbar cartItemCount={cartItems.length} />

        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/profile/:currentUser" element={<ProfilePage />} />
          <Route path="/register" element={<Register />} />
          <Route path="/wishlist" element={<Wishlist />} />
          <Route path="/#products-section" element={<HomePage />} />
          <Route
            path="/"
            element={
              <HomePage
                products={products}
                searchQuery={searchQuery}
                setSearchQuery={setSearchQuery}
                onProductClick={handleProductClick}
                loading={isLoading}
              />
            }
          />
          <Route
            path="/cart"
            element={
              <Cart
                cartItems={cartItems}
                removeItem={removeCartItem}
                removeAll={removeAll}
                setCartItems={setCartItems}
                updatedQuantity={updateQuantity}
                product={products}
                onProductClick={handleProductClick}
              />
            }
          />
          <Route
            path="/checkout"
            element={
              <CartCheckout
                cartItems={cartItems}
                orderDetails={orderDetails}
                setOrderDetails={setOrderDetails}
                selectedItems={selectedItems}
                totalSubtotals={totalSubtotals}
              />
            }
          />
          <Route
            path="/product/:id/checkout/:quantity"
            element={
              <ProductCheckout
                orderDetails={orderDetails}
                setOrderDetails={setOrderDetails}
              />
            }
          />
          <Route
            path="/orderConfirm"
            element={
              <OrderConfirm
                orderDetails={orderDetails}
                selectedItems={selectedItems}
                totalSubtotals={totalSubtotals}
              />
            }
          />
          <Route
            path="/order-confirm/:id/:quantity"
            element={
              <ProductOrderConfirm
                orderDetails={orderDetails}
                totalSubtotals={totalSubtotals}
              />
            }
          />
          <Route
            path="/product/:id"
            element={
              <ProductDetails
                isLoading={isLoading}
                setIsLoading={setIsLoading}
                addToCart={addToCart}
              />
            }
          ></Route>
        </Routes>
        <Footer />
      </BrowserRouter>
    </div>
  );
}
export default App;
