import { useState } from "react";
import CartProduct from "../components/CartProduct";

function Cart({ cartItems, removeItem }) {
  const [quantity, setQuantity] = useState(1);
  const [items, setItems] = useState(cartItems);

  const handleQuantityChange = (itemId, newQuantity) => {
    const updatedItems = items.map((item) => {
      if (item.id === itemId) {
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
  };

  const totalSubtotal = cartItems.reduce((total, item) => {
    return total + item.quantity * item.price;
  }, 0);

  return (
    <div className="cart-section">
      <>
        <div className="cart">
          <h1>Welcome to the Cart!</h1>
          <p>Make sure you add items soon! ðŸ˜‰</p>
        </div>
        <table className="cart-table">
          <thead>
            <tr>
              {/* <th>Img</th> */}
              <th className="product-column">Product</th>
              <th>Price</th>
              <th>Qty</th>
              <th>Subtotal</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item) => (
              <CartProduct
                key={item.id}
                item={item}
                removeItem={removeItem}
                quantity={quantity}
                dec={dec}
                inc={inc}
              />
            ))}
          </tbody>
        </table>
        <div className="cart-checkout">
          <div className="coupon">
            <input type="text" placeholder="Enter Coupon Code" />
            <button>Apply Coupon</button>
          </div>
          <div></div>
          <div className="cart-total">
            <h3>Cart Total</h3>
            <div className="details">
              <div className="flex detail">
                <span>Subtotal</span>
                <span>${totalSubtotal}</span>
              </div>
              <div className="flex detail">
                <span>Shipping</span>
                <span>Free</span>
              </div>
              <div className="flex detail total">
                <span>Total</span>
                <span>$1750</span>
              </div>
            </div>

            <div className="btn-checkout">
              <button>Proceed to checkout</button>
            </div>
          </div>
        </div>
      </>
    </div>
  );
}

export default Cart;
