import { Link } from "react-router-dom";

function CartProduct({
  item,
  removeItem,
  updatedQuantity,
  toggleItemSelection,
  product,
}) {
  const dec = () => {
    if (item.quantity < 2) return;
    updatedQuantity(item.id, item.quantity - 1);
  };

  const inc = () => {
    updatedQuantity(item.id, item.quantity + 1);
  };

  return (
    <tr className="cart-item">
      <td>
        <input
          type="checkbox"
          checked={item.isChecked}
          onChange={() => toggleItemSelection(item.id)}
        />
      </td>
      <td>
        <Link to={`/product/${product.id}`} colSpan="2" className="productImg">
          <img src={item.image} alt={item.title} />
          <h3>{item.title}</h3>
        </Link>
      </td>
      <td>
        <p>$ {item.price}</p>
      </td>
      <td>
        <div className="quantity">
          <button onClick={dec}>-</button>
          <span>{item.quantity}</span>
          <button onClick={inc}>+</button>
        </div>
      </td>
      <td>
        <p>$ {Number(item.subtotal).toFixed(2)}</p>
      </td>
      <td>
        <button
          className="bg-transparent border-2 border-orange-700 rounded-lg text-black text-2xl py-2 px-4"
          onClick={() => removeItem(item)}
        >
          &times;
        </button>
      </td>
    </tr>
  );
}

export default CartProduct;
