import { useState } from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";

function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const handleRegister = async (email, password) => {
    try {
      await firebase.auth().createUserWithEmailAndPassword(email, password);
      // User registered successfully
    } catch (error) {
      console.log("Registration Failed");
    }
  };

  return (
    <div className="text-center h-screen grid grid-cols-2 place-items-center">
      <div className="bg-slate-600 h-full w-full text-7xl grid place-items-center">
        Hi!
      </div>
      <div className="">
        <h1 className="mb-8 text-2xl">Sign Up</h1>
        <form className="flex flex-col gap-4 border-2 border-blue-400 rounded-lg px-4 py-4">
          <div>
            <input
              type="text"
              placeholder="Email"
              value={email}
              required
              onChange={() => setEmail(e.target.value)}
              className="border border-gray-500 rounded-md py-2 px-2"
            />
          </div>
          <div>
            <input
              type="text"
              placeholder="password"
              value={password}
              required
              onChange={() => setPassword(e.target.value)}
              className="border border-gray-500 rounded-md py-2 px-2"
            />
          </div>
          <button>Sign Up</button>
        </form>
      </div>
    </div>
  );
}

export default Register;
