import { Link } from "react-router-dom";
import { useState } from "react";
import Loader from "./Loader";

function ProductItem({ product, onProductClick, loading }) {
  return (
    <li onClick={() => onProductClick(product.id)} className="border-b">
      {loading ? (
        <Loader />
      ) : (
        <>
          <Link
            to={`/product/${product.id}`}
            className="flex flex-col gap-4 cursor-pointer"
          >
            <img
              src={product.image}
              alt={product.title}
              className="h-60 border border-gray-500 rounded-md p-4 shadow-xl"
            />
            <div className="flex flex-col">
              <h2 className="font-semibold mb-2 text-gray-600">
                {product.title}
              </h2>
              <div className="flex justify-between items-center mt-auto">
                <p className="">
                  ${" "}
                  <span className="text-xl font-semibold">{product.price}</span>
                </p>
                <p className="rating">Rating: ‚≠ê{product.rating.rate}</p>
              </div>
            </div>
          </Link>
        </>
      )}
    </li>
  );
}

export default ProductItem;
